<?php# INITerror_reporting(-1);ini_set('error_log', 1);ini_set('display_errors', 1);_::init(array(    'templates_dir' => './templates'));# LOG$log = _::Log();_::Registry()->set('log', $log);function l($msg, $lvl = _Log::PROD) {    _::Registry()->get('log')->log($msg, $lvl);}# REQUEST$cfg =_::Config()->config;_::Request($cfg->relpath[DEVELOP]);# ROUTES$routes = array(    '' => array('index', 'index'), // Default route	//'admin' => array('admin', 'index'),    '#comment/(\d{4})/(\d{2})/(\d{2})/(.+)#' => array('comment', 'index', array('file' => '$1_$2_$3_$4')),    //'#show/(\d+)/.+/?#' => array('article', 'show', array('id' => '$1')),    //'go/:id/aa' => array('url', 'show', array('id' => ':id')),);_::Router($routes, array('ext' => '.html'))->route();# AUTH_::Auth()->setup(new AuthBackendArray(array('admin' => array('password' => '1234', 'role' => 'admin'))));# CACHE$cache = _::Cache(new CacheBackendFile('./cache/cachebackend/', array('gc_prob' => 100)));_::Registry()->set('cache', $cache);# ACL$acl = _::ACL();$acl->addRole('mod');$acl->addRole('admin', 'mod');$acl->allow('admin', 'admin');# VIEW$view = new _View();$view->setLayout('layout');_::Controller()->setView($view);# CONTROLLER_::Controller()->registerSlot('sidebar', 'slots/sidebar.php', 'templates/_aside.phtml');_::Controller()->go();